// 配置vue的路由
import Vue from 'vue';
import Bar from './components/Bar.vue';
import Foo from './components/Foo.vue'
import VueRouter from 'vue-router';
import VueMeta from 'vue-meta';

Vue.use(VueMeta); // this.$meta
Vue.use(VueRouter);
export default ()=>{
    let router =  new VueRouter({
        mode:'history',
        routes:[
            {path:'/',component:Bar},
            {path:'/foo',component:Foo}
        ]
    });
    return router;
}

import Vue from 'vue';
import App from '../App.vue';
import createRouter from '../router';
import createStore from '../store';
import request from './request';
// 为了兼容服务端 要把这个方法改造成函数
import bus from '../libs/eventBus';
export default () => { 
    // 创建实例的函数
    let router = createRouter();
    let store = createStore(request);
    let app = new Vue({
        router,
        store,
        render:(h)=>h(App)
    });
    return {
        app,
        router,
        store
    };
}

import createApp from './app';
// 服务端会调用此函数 产生一个新的app实例
export default (context)=>{
    return new Promise((resolve,reject)=>{
        let {app,router,store} = createApp();
        router.push(context.url); // 跳转到路由
        // 如果服务端 启动时 直接访问 /foo  返回的页面永远都是 index.html 需要通过路由跳转到指定路径
        // 为了防止路由中的异步逻辑 所以采用 promise的形式 等待路由加载完成后 返回vue实例，服务端才可以渲染出完整的页面


        // 需要把当前页面中匹配到的组件 找到他的asyncData方法让他执行

        router.onReady(()=>{
            // 获取当前路径匹配到的组件  看一下这个组件中 有没有 asyncData方法
            let matchesComponents = router.getMatchedComponents();
            Promise.all(matchesComponents.map(component=>{
                if(component.asyncData){
                    return component.asyncData({store});
                }
            })).then(()=>{
                // 把vuex中的状态 挂载在 上下文中的state上
                context.state = store.state;
                context.meta = app.$meta(); // 不能解构
                // 会自动在window上挂载一个属性__INITIAL_STATE__
                resolve(app);
            }).catch(err=>{
                console.log(err);
            })
        });
    });
};

import axios from 'axios';
//创建一个axios的实例, 配置baseURL的基准路径
//服务 器端访问的话是访问的 4000
export default (req)=>axios.create({
    baseURL:'http://localhost:4000',
    headers:{
        cookie:req.get('cookie')||''
    }
});

import Vue from 'vue';

import Vuex from 'vuex';


Vue.use(Vuex);

export default (request)=>{
    let store = new Vuex.Store({
        state: {
            username:'jw',
            tagList: []
        },
        mutations:{
            set_username(state){
                state.username = 'zfpx1';
            },
            set_tag_list(state,list){
                state.tagList = list;
            }
        },
        getters: {
            tagList({tagList}) {
                return tagList;
            }
        },
        actions:{
            set_username({commit}){
                return new Promise((resolve,reject)=>{
                    setTimeout(() => {
                        commit('set_username');
                        resolve();
                    }, 1000);
                });
            },
            set_tag_list({commit}){
                return request.get('/api/tagList').then(function(result){
                    if (result.status===200) {
                        let list  = result.data;
                        commit('set_tag_list',list);
                    }
                });
            }
        }
    });
    if(typeof window !== 'undefined' && window.__INITIAL_STATE__){
        store.replaceState(window.__INITIAL_STATE__);
    }
    return store;
}
